import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'firstApp';
  inputValue:string = "Initial Value";
  todos = [
    { id: 1, task: 'swimming',venue1:"marena",venue2:"sundar",completed:false },
    { id: 2, task: 'reading',venue1:"library",venue2:"hostel",completed:false },
    { id: 3, task: 'go to gym',venue1:"SMIT",venue2:"Out of Campus",completed:false }
  ];
  toggleTodoCompleted(todo: any) {
    todo.completed = !todo.completed;
  }
}

<div>
  <h1>Two Way Data-Binding</h1>
  <ul style="list-style-type: none;">
     <li *ngFor="let todo of todos">
       <input type="checkbox" [checked]="todo.completed" (change)="toggleTodoCompleted(todo)">
       <span>{{ todo.task }}</span>
       <div *ngIf="todo.completed">'{{todo.task}}' task is completed
          <p *ngIf="todo.task == 'swimming'">Marena<br>Sundar Resort</p>
          <p *ngIf="todo.task == 'breakfast'">Mess<br>Froyo</p>
          <p *ngIf="todo.task == 'office'">A-Block<br>C-Block</p>
          <p *ngIf="todo.task == 'workout'">Back<br>Chest</p>
          <p *ngIf="todo.task == 'dinner'">Rice & Meat<br>Momo</p>
       </div>
     </li>
   </ul>
</div>
